package org.year_2024.april.easy;

import org.junit.jupiter.api.TestInstance;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.params.provider.Arguments.arguments;

@TestInstance(TestInstance.Lifecycle.PER_CLASS)
class FindTheDifferenceTest {
    private final FindTheDifference findTheDifference = new FindTheDifference();

    @ParameterizedTest
    @MethodSource(value = "testCases")
    void testCases(String s, String t, char expected) {
        assertEquals(expected, findTheDifference.findTheDifference(s, t));
    }

    Stream<Arguments> testCases() {
        return Stream.of(
                arguments("abcd", "abcde", 'e'),
                arguments("", "y", 'y'),
                arguments("a", "aa", 'a'),
                arguments("",
                        "",
                        "t")
        );
    }
}